import React, { useReducer } from "react";

export const todoReducer = (state, action) => {
  switch (action.type) {
    case "ADD-TODO":
      return [
        ...state,
        { text: action.payload, id: Date.now(), completed: false }
      ];
    default:
      return state;
  }
};
