import React, { useReducer } from "react";

const todo = [];

const todoReducer = (state, action) => {
  console.log("my action", action);
  switch (action.type) {
    case "ADD_TODO":
      return [
        ...state,
        { text: action.payload, id: Date.now(), completed: false }
      ];
    //   case "TOGGLE_TODO":
    //       return state.map(todo=>{
    //           if(todo.id === )
    //       });
    default:
      return state;
  }
};

export { todo };
export { todoReducer };
